from fastapi.middleware.wsgi import WSGIMiddleware
from flask import Flask, send_from_directory, session, request, redirect, url_for
from flask_session import Session
import identity, identity.web
import requests
from pathlib import Path
import os, re

def SinglePageApplication(directory: str):
    application_client_id = os.getenv('OPENPECTUS_APPLICATION_CLIENT_ID')
    directory_tenant_id = os.getenv('OPENPECTUS_DIRECTORY_TENANT_ID')
    client_secret = os.getenv('OPENPECTUS_CLIENT_SECRET')
    app = Flask(__name__)

    app.config['AUTHORITY'] = f'https://login.microsoftonline.com/{directory_tenant_id}'
    app.config['SESSION_TYPE'] = 'filesystem'
    app.config['SERVER_NAME'] = os.getenv('OPENPECTUS_DOMAIN_NAME')
    app.config['REQUIRE_AUTHENTICATON'] = application_client_id and directory_tenant_id and client_secret
    app.config['PREFERRED_URL_SCHEME'] = 'https' if app.config['REQUIRE_AUTHENTICATON'] else 'http' # Azure authentication requires https
    Session(app)

    from werkzeug.middleware.proxy_fix import ProxyFix
    app.wsgi_app = ProxyFix(app.wsgi_app, x_proto=1, x_host=1)

    auth = identity.web.Auth(
        session=session,
        authority=app.config['AUTHORITY'],
        client_id=application_client_id,
        client_credential=client_secret,
    )

    def require_authentication(func):
        '''
        Decorator function to require authentication.
        '''
        def inner(*args, **kwargs):
            if app.config['REQUIRE_AUTHENTICATON'] and not auth.get_user():
                auth_process = auth.log_in(redirect_uri=url_for('msal_token', _external=True))
                return redirect(auth_process['auth_uri'])
            return func(*args, **kwargs)
        inner.__name__ = func.__name__ # https://stackoverflow.com/questions/17256602/
        return inner

    @app.route('/msal-token', methods=['GET'])
    def msal_token():
        '''
        Return MSAL token for authentication workflow.
        '''
        result = auth.complete_log_in(request.args)
        if 'error' in result:
            return {'auth_error': result}
            #render_template('auth_error.html', result=result)
            return result
        else:
            return redirect(url_for('index'))

    @app.route('/logout', methods=['GET'])
    def logout():
        '''
        Log out of this app. This will not log user out of SSO.
        If you do want to log the user completely out of the SSO
        then you need to do like this:
        return redirect(auth.log_out(url_for('index', _external=True)))
        '''
        del session['_logged_in_user']
        return redirect(url_for('index'))

    @app.route('/id', methods=['GET'])
    @require_authentication
    def id():
        '''
        Return dict with user information
        '''
        result = dict(
        initials = 'NA',
        full_name = 'Not Applicable',
        profile_picture = 'data:image/jpeg;base64,',
        roles = [],
        )
        if not app.config['REQUIRE_AUTHENTICATON']:
            return result
        token = auth.get_token_for_user([])

        if 'profile_picture' not in session:
            api_result = requests.post('https://graph.microsoft.com/v1.0/$batch', json={
            'requests': [
                dict(id=1, method='GET', url='/me/photo/$value'),
                ]
            },
            headers={'Authorization': 'Bearer ' + token['access_token']}).json()
            data = {element['id']: element for element in api_result['responses']}
            profile_picture_type = data['1']['headers']['Content-Type']
            profile_picture_data_base64 = data['1']['body']
            session['profile_picture'] = f'data:{profile_picture_type};base64, {profile_picture_data_base64}'

        user = auth.get_user()
        result['roles'] = user['roles']
        result['profile_picture'] = session['profile_picture']
        m = re.match(r'(?P<initials>.{2,4}) \((?P<name>.*?)\)', user['name'])
        if m:
            result['initials'] =  m.groupdict()['initials'].upper()
            result['full_name'] = m.groupdict()['name']
        return result

    @app.route('/')
    @app.route('/<path:path>')
    @require_authentication
    def index(path='index.html', method=['GET']):
        path = Path(path)
        if not os.path.isfile(directory / path):
            path = Path('index.html')
        return send_from_directory(directory, path)

    return WSGIMiddleware(app)
